INSERT INTO `choice_learning_details` VALUES (1,1141,'• Organizes data efficiently for multiple users\n• Provides fast and reliable data retrieval\n• Ensures secure and controlled access','• Requires proper database design\n• Needs consistent maintenance and backups\n• Depends on indexing for performance','2025-11-30 20:31:08'),(2,1145,'• Guarantees unique identification of records\n• Improves query and lookup performance\n• Enforces strong data integrity','• Cannot contain NULL values\n• Must remain stable to avoid relational issues\n• Poorly chosen keys reduce performance','2025-11-30 20:31:08'),(3,1149,'• Allows full control of table structure\n• Supports precise datatype and constraint definition\n• Promotes well-organized database design','• Requires thoughtful planning before creation\n• Structural mistakes are costly to change\n• Overcomplicated schemas reduce performance','2025-11-30 20:31:08'),(4,1153,'• Ensures relational consistency across tables\n• Prevents orphaned or invalid references\n• Helps maintain structured multi-table data','• May slow insert/update operations due to checks\n• Requires careful indexing strategies\n• Cascading rules must be planned properly','2025-11-30 20:31:08'),(5,1157,'• Reduces redundant data storage\n• Prevents anomalies during updates and deletions\n• Improves overall data consistency','• Excessive normalization increases join complexity\n• May reduce query performance if overused\n• Requires strong understanding of schema design','2025-11-30 20:31:08'),(6,1161,'• Eliminates repeating groups and multivalued columns\n• Creates cleaner and more predictable table structures\n• Simplifies data entry and validation','• Does not remove deeper dependency issues\n• Sometimes increases the number of tables\n• Requires additional normalization for full optimization','2025-11-30 20:31:08'),(7,1165,'• Allows precise filtering of query results\n• Reduces unnecessary data returns\n• Improves performance on large datasets','• Poorly written conditions slow queries\n• Requires understanding of logical operators\n• Must be indexed properly for efficiency','2025-11-30 20:31:08'),(8,1169,'• Retrieves only matching data between tables\n• Efficient and commonly used join type\n• Helps ensure accurate relational results','• Requires proper key relationships\n• Query cost increases with large tables\n• Missing indexes lead to slow performance','2025-11-30 20:31:08'),(9,1173,'• Ensures reliable and predictable transactions\n• Prevents inconsistent or partial changes\n• Maintains long-term data integrity','• Transaction locks can affect concurrency\n• Requires storage support for durability\n• Improper use may reduce performance','2025-11-30 20:31:08'),(10,1177,'• Allows controlled modification of existing data\n• Ideal for correcting or updating records\n• Works efficiently on indexed tables','• Incorrect updates may corrupt data\n• Requires strict WHERE filtering\n• Large batch updates can cause locking','2025-11-30 20:31:08'),(11,1181,'• Ensures critical fields always contain a value\n• Prevents incomplete or invalid data entry\n• Strengthens data accuracy','• Must be combined with validation rules\n• Cannot be applied to optional fields\n• May require default values to avoid errors','2025-11-30 20:31:08'),(12,1185,'• Improves read/query performance significantly\n• Enables fast lookup based on key columns\n• Optimizes large dataset operations','• Over-indexing slows inserts and updates\n• Requires periodic maintenance\n• Uses additional storage space','2025-11-30 20:31:08'),(13,1189,'• Permanently removes an entire table\n• Clears unused or obsolete structures\n• Helps maintain a clean database schema','• Data removed cannot be recovered\n• May break dependencies with other tables\n• Requires caution in production systems','2025-11-30 20:31:08'),(14,1193,'• Retrieves all rows from the left table\n• Ensures no data loss from the primary table\n• Useful when partial matches are acceptable','• May produce large result sets\n• Can hide referential issues\n• Requires indexing on join keys','2025-11-30 20:31:08'),(15,1197,'• Removes partial dependencies\n• Improves logical data organization\n• Reduces update anomalies','• Requires understanding of functional dependencies\n• Over-normalization may hinder performance\n• Often increases join complexity','2025-11-30 20:31:08'),(16,1201,'• Enables unique identification using multiple fields\n• Useful when no single attribute is unique\n• Supports flexible key design','• Can become large and inefficient\n• Harder to use in foreign key references\n• Changes in any column affect the key','2025-11-30 20:31:08'),(17,1205,'• Modifies table structures without recreation\n• Supports adding/removing columns easily\n• Essential for iterative schema evolution','• Frequent changes can affect stability\n• May require downtime for large tables\n• Wrong modifications can break constraints','2025-11-30 20:31:08'),(18,1209,'• Allows sorting of query results\n• Improves readability and organization\n• Supports both ascending and descending order','• Sorting adds processing cost\n• Large datasets require indexing\n• Overuse can degrade performance','2025-11-30 20:31:08'),(19,1213,'• Simplifies complex queries into reusable objects\n• Provides abstraction for security\n• Reduces repeated query logic','• Views may become slow with complex logic\n• Cannot always be indexed\n• Requires synchronization with schema changes','2025-11-30 20:31:08'),(20,1217,'• Guarantees unique values in specific columns\n• Prevents accidental duplicates\n• Strengthens overall data reliability','• Requires proper indexing\n• May cause insert conflicts\n• Needs clear understanding of uniqueness rules','2025-11-30 20:31:08'),(21,1221,'• Ensures transaction results persist after commit\n• Prevents accidental loss of completed changes\n• Enhances reliability of mission-critical systems','• Requires robust storage management\n• May increase write costs\n• Needs proper configuration for durability','2025-11-30 20:31:08'),(22,1225,'• Quickly removes all rows from a table\n• Faster than DELETE due to minimal logging\n• Frees storage space efficiently','• Cannot be rolled back in some systems\n• Cannot use WHERE filtering\n• Requires caution on production data','2025-11-30 20:31:08'),(23,1229,'• Combines all matched and unmatched rows\n• Useful for complex reporting needs\n• Ensures no data loss from either table','• Produces very large results\n• Requires strong indexing\n• Can be expensive to execute','2025-11-30 20:31:08'),(24,1233,'• Improves long-term database health\n• Reduces logical design flaws\n• Enhances maintainability and performance','• Requires deep analysis effort\n• May involve restructuring\n• Needs experienced designers','2025-11-30 20:31:08'),(25,1237,'• Provides quick row counts\n• Useful for reporting and summaries\n• Works well with GROUP BY','• Can be slow on large tables\n• Requires indexing for performance\n• COUNT(*) may scan entire tables','2025-11-30 20:31:08'),(26,1241,'• Saves all changes permanently\n• Marks the end of a successful transaction\n• Ensures stable and predictable updates','• Cannot undo after commit\n• Requires careful transaction control\n• Misuse may cause unintended changes','2025-11-30 20:31:08'),(27,1245,'• Provides multiple candidate choices for primary keys\n• Ensures strong data uniqueness\n• Improves flexibility in schema design','• Must choose the most stable candidate\n• Poor selection impacts performance\n• Requires understanding of functional dependencies','2025-11-30 20:31:08'),(28,1249,'• Simplifies adding new rows to tables\n• Works efficiently with predefined structures\n• Supports batch insertion of multiple records','• Requires matching column order or names\n• Missing/extra values cause errors\n• Poor input validation leads to bad data','2025-11-30 20:31:08'),(29,1253,'• Groups related rows for aggregation\n• Enables meaningful summary results\n• Works well with COUNT, SUM, AVG and more','• Requires indexed grouping columns\n• Incorrect grouping gives wrong results\n• Large groups can slow processing','2025-11-30 20:31:08'),(30,1257,'• Ensures unique artificial identifiers\n• Avoids dependency on business data\n• Simplifies foreign key references','• Must avoid exposing surrogate keys externally\n• Does not guarantee meaningful identifiers\n• Requires careful mapping in applications','2025-11-30 20:31:08');
/*!40000 ALTER TABLE `choice_learning_details` ENABLE KEYS */;
UNLOCK TABLES;

--
-- Table structure for table `choices`
--

DROP TABLE IF EXISTS `choices`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `choices` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `choice` text NOT NULL,
  `question_id` int(11) DEFAULT NULL,
  `is_correct` tinyint(1) DEFAULT 0,
  PRIMARY KEY (`id`),
  KEY `question_id` (`question_id`),
  CONSTRAINT `choices_ibfk_1` FOREIGN KEY (`question_id`) REFERENCES `questions` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=1261 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

