--
-- Table structure for table `registration_code_uses`
--

DROP TABLE IF EXISTS `registration_code_uses`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `registration_code_uses` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `registration_code_id` int(11) NOT NULL,
  `student_id` int(11) NOT NULL,
  `used_at` datetime DEFAULT current_timestamp(),
  PRIMARY KEY (`id`),
  KEY `registration_code_id` (`registration_code_id`),
  KEY `student_id` (`student_id`),
  CONSTRAINT `registration_code_uses_ibfk_1` FOREIGN KEY (`registration_code_id`) REFERENCES `registration_codes` (`id`) ON DELETE CASCADE,
  CONSTRAINT `registration_code_uses_ibfk_2` FOREIGN KEY (`student_id`) REFERENCES `users` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
